    fun openBrowser(context: Context,url: String?) {
        val browserIntent =
            Intent(Intent.ACTION_VIEW, Uri.parse(url))
        context.startActivity(browserIntent)
    }

    fun openDual(context: Context,mobileNumber: String?) {
        val intent = Intent()
        intent.action = Intent.ACTION_DIAL // Action for what intent called for
        intent.data =
            Uri.parse("tel: $mobileNumber") // Data with intent respective action on intent
        context.startActivity(intent)
    }

    fun isValidPassword(password: String): Boolean {

        if (password.length < 6)
            return false
        return true
    }

    fun calculateNoOfColumns(
        context: Context,
        columnWidthDp: Float
    ): Int { // For example columnWidthdp=180
        val displayMetrics = context.resources.displayMetrics
        val screenWidthDp = displayMetrics.widthPixels / displayMetrics.density
        return (screenWidthDp / columnWidthDp + 0.5).toInt()
    }


    fun loadGlideImage(context: Context?, url: String?, placeholder: Int, imageView: ImageView?) {
        Glide.with(context!!)
            .load(url).apply(
                RequestOptions()
                    .centerCrop()
                    .dontAnimate()
                    .dontTransform()
                    .placeholder(placeholder)

            ).listener(object : RequestListener<Drawable?> {
                override fun onLoadFailed(
                    e: GlideException?,
                    model: Any,
                    target: Target<Drawable?>,
                    isFirstResource: Boolean
                ): Boolean {
                    return false
                }

                override fun onResourceReady(
                    resource: Drawable?,
                    model: Any,
                    target: Target<Drawable?>,
                    dataSource: DataSource,
                    isFirstResource: Boolean
                ): Boolean {
                    //    progressBar.visibility = View.GONE
                    return false
                }
            })
            .into(imageView!!)
    }




    //Get Current Month
     fun getCurrentMonth(): Int {
        return Calendar.getInstance()[Calendar.MONTH]
    }
    //Get Current Year

    fun getCurrentYear(): Int {
        return Calendar.getInstance()[Calendar.YEAR]
    }

    fun getDeviceId(context: Context): String? {
        return Settings.Secure.getString(
            context.contentResolver,
            Settings.Secure.ANDROID_ID
        )
    }

    fun getCurrentDay(): Int {
        return Calendar.getInstance()[Calendar.DAY_OF_MONTH]
    }



