    fun openBrowser(context: Context, url: String?) {
        val browserIntent =
            Intent(Intent.ACTION_VIEW, Uri.parse(url))
        context.startActivity(browserIntent)
    }

    fun openDual(context: Context, mobileNumber: String?) {
        val intent = Intent()
        intent.action = Intent.ACTION_DIAL // Action for what intent called for
        intent.data =
            Uri.parse("tel: $mobileNumber") // Data with intent respective action on intent
        context.startActivity(intent)
    }

    fun isValidPassword(password: String): Boolean {

        if (password.length < 6)
            return false
        return true
    }

    fun calculateNoOfColumns(
        context: Context,
        columnWidthDp: Float
    ): Int { // For example columnWidthdp=180
        val displayMetrics = context.resources.displayMetrics
        val screenWidthDp = displayMetrics.widthPixels / displayMetrics.density
        return (screenWidthDp / columnWidthDp + 0.5).toInt()
    }


    fun loadGlideImage(context: Context?, url: String?, placeholder: Int, imageView: ImageView?) {
        Glide.with(context!!)
            .load(url).apply(
                RequestOptions()
                    .centerCrop()
                    .dontAnimate()
                    .dontTransform()
                    .placeholder(placeholder)

            ).listener(object : RequestListener<Drawable?> {
                override fun onLoadFailed(
                    e: GlideException?,
                    model: Any,
                    target: Target<Drawable?>,
                    isFirstResource: Boolean
                ): Boolean {
                    return false
                }

                override fun onResourceReady(
                    resource: Drawable?,
                    model: Any,
                    target: Target<Drawable?>,
                    dataSource: DataSource,
                    isFirstResource: Boolean
                ): Boolean {
                    //    progressBar.visibility = View.GONE
                    return false
                }
            })
            .into(imageView!!)
    }


    //Get Current Month
    fun getCurrentMonth(): Int {
        return Calendar.getInstance()[Calendar.MONTH]
    }
    //Get Current Year

    fun getCurrentYear(): Int {
        return Calendar.getInstance()[Calendar.YEAR]
    }

    fun getDeviceId(context: Context): String? {
        return Settings.Secure.getString(
            context.contentResolver,
            Settings.Secure.ANDROID_ID
        )
    }

    fun getCurrentDay(): Int {
        return Calendar.getInstance()[Calendar.DAY_OF_MONTH]
    }


    fun getAddress(
        handler: Handler?,
        context: Context?,
        latitude: Double,
        longitude: Double
    ) {
        val thread: Thread = object : Thread() {
            override fun run() {
                val geocoder = Geocoder(
                    context,
                    Locale(MyApp.getApp().appPreferencesHelper.getLang())
                )
                var result: String? = null
                var address: Address? = null
                try {
                    val addressList =
                        geocoder.getFromLocation(latitude, longitude, 1)
                    if (addressList != null && addressList.size > 0) {
                        address = addressList[0]
                        result = address.locality + "," + address.adminArea
                    }
                } catch (e: IOException) {
                    Log.e("Location Address Loader", "Unable connect to Geocoder", e)
                } finally {
                    val message = Message.obtain()
                    message.target = handler
                    val sb = StringBuilder()
                    if (result != null) {
                        message.what = 1
                        val bundle = Bundle()
                        val add = address!!.getAddressLine(0)
                        bundle.putString(
                            "address",
                            add
                        )
                        message.data = bundle
                    } else {
                        message.what = 1
                        val bundle = Bundle()
                        result = " Unable to get address for this location."
                        bundle.putString("address", result)
                        message.data = bundle
                    }
                    message.sendToTarget()
                }
            }
        }
        thread.start()
    }

    fun newFacebookIntent(pm: PackageManager, url: String): Intent? {
        var uri = Uri.parse(url)
        try {
            val applicationInfo = pm.getApplicationInfo("com.facebook.katana", 0)
            if (applicationInfo.enabled) {
                // http://stackoverflow.com/a/24547437/1048340
                uri = Uri.parse("fb://facewebmodal/f?href=$url")
            }
        } catch (ignored: PackageManager.NameNotFoundException) {
        }
        return Intent(Intent.ACTION_VIEW, uri)
    }

    fun newWhatsIntent(pm: PackageManager, phone: String): Intent? {
        try {
            val intent = Intent(Intent.ACTION_VIEW)
            val url =
                "https://api.whatsapp.com/send?phone=$phone&text=" + URLEncoder.encode(
                    "Hii",
                    "UTF-8"
                )
            intent.setPackage("com.whatsapp")
            intent.data = Uri.parse(url)
            if (intent.resolveActivity(pm) != null) {
                return intent
            }
        } catch ( e:Exception) {
            return null
        }
        return null
    }

    fun newInstagramProfileIntent(pm: PackageManager, url: String): Intent? {
        var url = url
        val intent = Intent(Intent.ACTION_VIEW)
        try {
            if (pm.getPackageInfo("com.instagram.android", 0) != null) {
                if (url.endsWith("/")) {
                    url = url.substring(0, url.length - 1)
                }
                val username = url.substring(url.lastIndexOf("/") + 1)
                // http://stackoverflow.com/questions/21505941/intent-to-open-instagram-user-profile-on-android
                intent.data = Uri.parse("http://instagram.com/_u/$username")
                intent.setPackage("com.instagram.android")
                return intent
            }
        } catch (ignored: PackageManager.NameNotFoundException) {
        }
        intent.data = Uri.parse(url)
        return intent
    }


